package looping;

public class L8_if {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 int i = 10;
		 
	        if (i < 15)
	            System.out.println("10 is less than 15");
	 
	        System.out.println("Outside if-block");
	        // both statements will be printed
	}

}
